currentUsers.map:
function(doc) 
					{
						if (!doc.userId)
							return;  //not a user

						if (!doc.saveTime)
							return;  //old test users, etc

						var docAge = (new Date()).getTime() - doc.saveTime ;
						if (docAge>100*1000) //100 sec in ms
							return;  //too old

						//got one!
						emit(doc.userId,doc); 
					}
